import React, { useState } from 'react';
import WindowInputs from './WindowInputs';
import { AddQuoteToLocalStorage, GetQuotesFromLocalStorage } from '../helpers/Helpers';
import { Segment, Form, Input, Button, Grid, Card, GridRow, GridColumn } from "semantic-ui-react";

// initial app state
var seedData = {
    quotes: [
        {
            "id": 1,
            "total": 450,
            "dateCreated": "2020-03-11T22:22:34.600Z",
            "email": "cbellee@home.net",
            "firstName": "Chris",
            "lastName": "Bellee",
            "street": "19 Hanover Avenue",
            "suburb": "North Epping",
            "city": "Sydney",
            "postCode": 2121,
            "phone": "0404168323",
            "windows": [
                {
                    "name": "Lounge A",
                    "height": 1200,
                    "width": 2400,
                    "notes": "add notes here...",
                    "price": 200,
                    "material": "wood",
                    "panels": 3
                },
                {
                    "name": "Lounge B",
                    "height": 1400,
                    "width": 2200,
                    "notes": "add notes here...",
                    "price": 250,
                    "material": "pvc",
                    "panels": 3
                }
            ]
        },
        {
            "id": 2,
            "dateCreated": "2020-02-14T33:33:54.400Z",
            "total": 650,
            "email": "dsmith@gmail.com",
            "firstName": "Dave",
            "lastName": "Smith",
            "street": "31 Boundary Road",
            "suburb": "North Epping",
            "city": "Sydney",
            "postCode": 2121,
            "phone": "041516494",
            "windows": [
                {
                    "name": "Bedroom A",
                    "height": 1200,
                    "width": 1200,
                    "notes": "add notes here...",
                    "price": 300,
                    "material": "wood",
                    "panels": 2
                },
                {
                    "name": "Lounge B",
                    "height": 1800,
                    "width": 3200,
                    "notes": "add notes here...",
                    "price": 350,
                    "material": "pvc",
                    "panels": 4
                }
            ]
        }
    ],
    loading: false,
    error: null
};

const dateCreated = new Date().toISOString();

const QuoteForm = () => {
    const [quoteState, setQuoteState] = useState({
        dateCreated: dateCreated,
        total: '',
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        street: '',
        suburb: '',
        city: '',
        postCode: '',
        windows: []
    });

    const handleQuoteChange = (e) => setQuoteState({
        ...quoteState,
        [e.target.name]: e.target.value,
    });

    const blankWindow = { name: '', width: '2400', height: '1200', panel: '2' };
    const [windowState, setWindowState] = useState([
        { ...blankWindow },
    ]);

    const addWindow = () => {
        setWindowState([...windowState, { ...blankWindow }]);
    };

    const handleWindowChange = (e) => {
        const updatedWindows = [...windowState];
        console.log(JSON.stringify(updatedWindows));
        updatedWindows[e.target.dataset.idx][e.target.className] = e.target.value;
        setWindowState(updatedWindows);
    };

    const onSubmit = () => {
        quoteState.windows = windowState;
        console.log("quote state: " + JSON.stringify(quoteState));
        AddQuoteToLocalStorage('quotes', quoteState, seedData);
    }

    return (
        <Form>
            <Segment>
                <Grid columns={2} padded="vertically">
                    <Grid.Column>
                        <Form.Field>
                            <label>First Name</label>
                            <input
                                label="First Name"
                                type="text"
                                name="firstName"
                                id="firstName"
                                value={quoteState.firstName}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Last Name</label>
                            <input
                                label="Last Name"
                                type="text"
                                name="lastName"
                                id="lastName"
                                value={quoteState.lastName}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Email</label>
                            <input
                                label="Email"
                                type="text"
                                name="email"
                                id="email"
                                value={quoteState.email}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Phone</label>
                            <input
                                label="Phone"
                                type="text"
                                name="phone"
                                id="phone"
                                value={quoteState.phone}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Suburb</label>
                            <input
                                label="Suburb"
                                type="text"
                                name="suburb"
                                id="suburb"
                                value={quoteState.suburb}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Street</label>
                            <input
                                label="Street"
                                type="text"
                                name="street"
                                id="street"
                                value={quoteState.street}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>City</label>
                            <input
                                label="City"
                                type="text"
                                name="city"
                                id="city"
                                value={quoteState.city}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>PostCode</label>
                            <input
                                label="Postcode"
                                type="text"
                                name="postCode"
                                id="postCode"
                                value={quoteState.postCode}
                                onChange={handleQuoteChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                </Grid>
            </Segment>
            <Grid>
                <Form.Field>
                    <Input
                        type="button"
                        value="Add Window"
                        onClick={addWindow}
                    />
                </Form.Field>
                <Form.Field>
                    <Input type="submit" value="Save Quote" onClick={onSubmit} />
                </Form.Field>
            </Grid>
            <Grid columns={1}>
                <Grid.Column>
                    {
                        windowState.map((val, idx) => (
                            <WindowInputs
                                key={`window-${idx}`}
                                idx={idx}
                                windowState={windowState}
                                handleWindowChange={handleWindowChange}
                            />
                        ))
                    }
                </Grid.Column>
            </Grid>
        </Form >
    );
};

export default QuoteForm;
