import React from 'react';
import PropTypes from 'prop-types';

const WindowInputs = ({ idx, windowState, handleWindowChange }) => {
    const nameId = `name-${idx}`;
    const widthId = `width-${idx}`;
    const heightId = `height-${idx}`;
    return (
        <div key={`window-${idx}`}>
            <label htmlFor={nameId}>{`Window #${idx + 1}`}</label>
            <input
                type="text"
                name={nameId}
                data-idx={idx}
                id={nameId}
                className="name"
                value={windowState[idx].name}
                onChange={handleWindowChange}
            />
            <label htmlFor={widthId}>Width</label>
            <input
                type="text"
                name={widthId}
                data-idx={idx}
                id={widthId}
                className="width"
                value={windowState[idx].width}
                onChange={handleWindowChange}
            />
            <label htmlFor={heightId}>Height</label>
            <input
                type="text"
                name={heightId}
                data-idx={idx}
                id={heightId}
                className="height"
                value={windowState[idx].height}
                onChange={handleWindowChange}
            />
        </div>
    );
};

WindowInputs.propTypes = {
    idx: PropTypes.number,
    windowState: PropTypes.array,
    handleWindowChange: PropTypes.func,
};

export default WindowInputs;
