import React, { useState } from 'react';
import WindowInputs from './WindowInputs';
import { Form, Input, Button, Grid, FormField } from "semantic-ui-react";

const QuoteForm = () => {
    const [quoteState, setQuoteState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        street: '',
        suburb: '',
        city: '',
        postCode: ''
    });

    const handleFormChange = (e) => setQuoteState({
        ...quoteState,
        [e.target.name]: [e.target.value],
    });

    const blankWindow = { width: '2400', height: '1200' };
    const [windowState, setWindowState] = useState([
        { ...blankWindow },
    ]);

    const addWindow = () => {
        setWindowState([...windowState, { ...blankWindow }]);
    };

    const handleWindowChange = (e) => {
        const updatedWindows = [...windowState];
        updatedWindows[e.target.dataset.idx][e.target.className] = e.target.value;
        setWindowState(updatedWindows);
    };

    const onSubmit = () => {
        console.log("window state: " + windowState);
    }

    return (
        <Form>
            <Grid columns={2} padded="vertically">
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="First Name"
                            type="text"
                            name="firstName"
                            id="firstName"
                            value={quoteState.firstName}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Last Name"
                            type="text"
                            name="lastName"
                            id="lastName"
                            value={quoteState.lastName}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Email"
                            type="text"
                            name="email"
                            id="email"
                            value={quoteState.email}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Suburb"
                            type="text"
                            name="suburb"
                            id="suburb"
                            value={quoteState.suburb}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Street"
                            type="text"
                            name="street"
                            id="street"
                            value={quoteState.street}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="City"
                            type="text"
                            name="city"
                            id="city"
                            value={quoteState.city}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Postcode"
                            type="text"
                            name="postcode"
                            id="postcode"
                            value={quoteState.postCode}
                            onChange={handleFormChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Row>
                    <Form.Field>
                        <Input
                            type="button"
                            value="Add Window"
                            onClick={addWindow}
                        />
                    </Form.Field>
                </Grid.Row>
            </Grid>
            <Grid columns={1}>
                <Grid.Row>
                    {
                        windowState.map((val, idx) => (
                            <WindowInputs
                                key={`window-${idx}`}
                                idx={idx}
                                windowState={windowState}
                                handleWindowChange={handleWindowChange}
                            />
                        ))
                    }
                </Grid.Row>
                <Form.Field>
                    <Input type="submit" value="Add Quote" onClick={onSubmit} />
                </Form.Field>
            </Grid>
        </Form >
    );
};

export default QuoteForm;
