import React, { useState } from 'react';
import WindowInputs from './WindowInputs';
import { Form, Input, Button, Grid } from "semantic-ui-react";

const QuoteForm = () => {
    const [quoteState, setQuoteState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        street: '',
        suburb: '',
        city: '',
        postCode: ''
    });

    const handleQuoteChange = (e) => setQuoteState({
        ...quoteState,
        [e.target.name]: [e.target.value],
    });

    const blankWindow = { name: '', width: '', height: '' };
    const [windowState, setWindowState] = useState([
        { ...blankWindow },
    ]);

    const addWindow = () => {
        setWindowState([...windowState, { ...blankWindow }]);
    };

    const handleWindowChange = (e) => {
        const updatedWindows = [...windowState];
        console.log(JSON.stringify(updatedWindows));
        updatedWindows[e.target.dataset.idx][e.target.className] = e.target.value;
        setWindowState(updatedWindows);
    };

    const onSubmit = () => {
        console.log("window state: " + windowState);
    }

    return (
        <Form>
            <Grid columns={2} padded="vertically">
                <Grid.Column>
                    <Form.Field>
                    <label>First Name</label>
                        <input
                            label="First Name"
                            type="text"
                            name="firstName"
                            id="firstName"
                            value={quoteState.firstName}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>Last Name</label>
                        <input
                            label="Last Name"
                            type="text"
                            name="lastName"
                            id="lastName"
                            value={quoteState.lastName}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>Email</label>
                        <input
                            label="Email"
                            type="text"
                            name="email"
                            id="email"
                            value={quoteState.email}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>Phone</label>
                        <input
                            label="Phone"
                            type="text"
                            name="phone"
                            id="phone"
                            value={quoteState.phone}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>Suburb</label>
                        <input
                            label="Suburb"
                            type="text"
                            name="suburb"
                            id="suburb"
                            value={quoteState.suburb}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>Street</label>
                        <input
                            label="Street"
                            type="text"
                            name="street"
                            id="street"
                            value={quoteState.street}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>City</label>
                        <input
                            label="City"
                            type="text"
                            name="city"
                            id="city"
                            value={quoteState.city}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                    <label>PostCode</label>
                        <input
                            label="Postcode"
                            type="text"
                            name="postCode"
                            id="postCode"
                            value={quoteState.postCode}
                            onChange={handleQuoteChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Row>
                    <Form.Field>
                        <input
                            type="button"
                            value="Add Window"
                            onClick={addWindow}
                        />
                    </Form.Field>
                </Grid.Row>
            </Grid>
            <Grid columns={2}>
                <Grid.Column>
                    {
                        windowState.map((val, idx) => (
                            <WindowInputs
                                key={`window-${idx}`}
                                idx={idx}
                                windowState={windowState}
                                handleWindowChange={handleWindowChange}
                            />
                        ))
                    }
                                <hr />
                </Grid.Column>
                <Grid.Row>
                    <Form.Field>
                        <Input type="submit" value="Add Quote" onClick={onSubmit} />
                    </Form.Field>
                </Grid.Row>
            </Grid>
        </Form >
    );
};

export default QuoteForm;
