import React, { useState } from 'react';
import './App.css';
import QuoteForm from './components/QuoteForm';
import Header from './components/Header'
import Footer from './components/Footer'
import MainMenu from './components/MainMenu'
import PostCodeContext from './components/PostCodeContext';
import PostCodeData from './helpers/PostCodeData';
import { GetQuotesFromLocalStorage } from './helpers/Helpers';
import { Message } from 'semantic-ui-react';

function App() {
  const postCodeDataHook = useState(PostCodeData);

  function SaveDatFile(localstorage) {
    localstorage.root.getFile("quotes.json", { create: true }, function (DataFile) {
      DataFile.createWriter(function (DataContent) {
        var blob = new Blob(JSON.stringify(quote), { type: "text/plain" });
        DataContent.write(blob);
      });
    });
  }

  window.onbeforeunload = function () {
    let quotes = GetQuotesFromLocalStorage('quotes');
    window.webkitRequestFileSystem(window.TEMPORARY, 1024 * 1024, SaveDatFile(quotes));
  }

  return (
    <PostCodeContext.Provider value={postCodeDataHook}>
      <div className="App">
        <Header />
        <MainMenu />
        <Footer />
      </div>
    </PostCodeContext.Provider>
  );
}

export default App;
