import React from 'react';
import PropTypes from 'prop-types';
import { Grid, Form, Input, Label, Segment } from "semantic-ui-react";
import { getByDisplayValue } from '@testing-library/react';

const WindowInputs = ({ idx, windowState, handleWindowChange }) => {
    const nameId = `name-${idx}`;
    const widthId = `width-${idx}`;
    const heightId = `height-${idx}`;
    const panelId = `panel-${idx}`;
    const materialId = `material-${idx}`;
    const priceId = `price-${idx}`;
    const notesId = `notes-${idx}`;
    const materialData = [{ text: 'wood', value: 'wood' }, { text: 'pvc', value: 'pvc' }];

    return (
        <div key={`window-${idx}`}>
            <Segment>
                <Grid columns={2} padded="vertically">
                    <Grid.Column>
                        <Form.Field>
                            <label>Name</label>
                            <input
                                name={nameId}
                                data-idx={idx}
                                id={nameId}
                                className="name"
                                value={windowState[idx].name}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Width</label>
                            <input
                                name={widthId}
                                data-idx={idx}
                                id={widthId}
                                className="width"
                                value={windowState[idx].width}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Height</label>
                            <input
                                name={heightId}
                                data-idx={idx}
                                id={heightId}
                                className="height"
                                value={windowState[idx].height}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Panels</label>
                            <input
                                name={panelId}
                                data-idx={idx}
                                id={panelId}
                                className="panel"
                                value={windowState[idx].panel}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Material</label>
                            <select
                                name={materialId}
                                data-idx={idx}
                                id={materialId}
                                value={{ materialData }}
                                className="material"
                                value={windowState[idx].material}
                                onChange={handleWindowChange}>
                                <option value="wood">Wood</option>
                                <option value="pvc">Pvc</option>
                            </select>
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Price</label>
                            <input
                                name={priceId}
                                data-idx={idx}
                                id={priceId}
                                className="panel"
                                value={windowState[idx].price}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column width={2}>
                        <Form.Field >
                            <label>Notes</label>
                            <input
                                name={notesId}
                                data-idx={idx}
                                id={notesId}
                                className="notes"
                                value={windowState[idx].notes}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                </Grid>
            </Segment>
            <br />
        </div >
    );
};

WindowInputs.propTypes = {
    idx: PropTypes.number,
    windowState: PropTypes.array,
    handleWindowChange: PropTypes.func,
};

export default WindowInputs;
