import React from 'react';
import PropTypes from 'prop-types';
import { Grid, Form, Input, Label, Segment } from "semantic-ui-react";
import { getByDisplayValue } from '@testing-library/react';

const WindowInputs = ({ idx, windowState, handleWindowChange }) => {
    const nameId = `name-${idx}`;
    const widthId = `width-${idx}`;
    const heightId = `height-${idx}`;
    const panelId = `panel-${idx}`;
    const materialId = `material-${idx}`;
    const priceId = `price-${idx}`;
    const notesId = `notes-${idx}`;
    const materialData = [{ text: 'wood', value: 'wood' }, { text: 'pvc', value: 'pvc' }];

    var minPanels = 1;
    var maxPanels = 4;
    var defaultPanels = 2;
    const panelData = [];
    const items = [];
    let itemList = [];

    function ValidateWindowInputFields(height, width) {
        var h = height;
        var w = width;

        if (height === "" || height < minHeight) {
            h = minHeight;
        }

        if (height > maxHeight) {
            h = maxHeight;
        }

        if (width === "" || width < minWidth) {
            w = minWidth;
        }

        if (width > maxWidth) {
            w = maxWidth;
        }

        return { height: h, width: w };
    }

    if (windowPanels.value === "") {
        windowPanels.value = defaultPanels;
    }

    for (var i = 1; i <= maxPanels; i++) {
        panelData.push({ text: i, value: i });
    }

    var res = ValidateWindowInputFields(windowHeight.value, windowWidth.value)

    let widthRatio = res.width / displayWidth;
    let newHeight = res.height / widthRatio;
    var panelSpacing = displayWidth / windowPanels.value;

    for (let i = 0; i < windowPanels.value - 1; i++) {
        items.push(itemList[i]);
    }

    return (
        <div key={`window-${idx}`}>
            <Segment>
                <Grid columns={3} padded="vertically">
                    <Grid.Column>
                        <Form.Field>
                            <label>Name</label>
                            <input
                                name={nameId}
                                data-idx={idx}
                                id={nameId}
                                className="name"
                                value={windowState[idx].name}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Panels</label>
                            <input
                                name={panelId}
                                data-idx={idx}
                                id={panelId}
                                className="panel"
                                value={windowState[idx].panel}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field>
                            <label>Width</label>
                            <input
                                name={widthId}
                                data-idx={idx}
                                id={widthId}
                                className="width"
                                value={windowState[idx].width}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Height</label>
                            <input
                                name={heightId}
                                data-idx={idx}
                                id={heightId}
                                className="height"
                                value={windowState[idx].height}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Material</label>
                            <select
                                name={materialId}
                                data-idx={idx}
                                id={materialId}
                                value={{ materialData }}
                                className="material"
                                value={windowState[idx].material}
                                onChange={handleWindowChange}>
                                <option value="wood">Wood</option>
                                <option value="pvc">Pvc</option>
                            </select>
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Field >
                            <label>Price</label>
                            <input
                                name={priceId}
                                data-idx={idx}
                                id={priceId}
                                className="panel"
                                value={windowState[idx].price}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                    <Grid.Row>
                        <div id="windowContainer" style={{ height: newHeight, width: displayWidth }}>
                            {items.map((value, index) => {
                                var pSpacing = (panelSpacing * (index + 1) - ((index + 1)));
                                return <div style={{ position: "relative", left: pSpacing }} key={index} className={'windowItem ' + value}>
                                </div>
                            })}
                        </div>
                    </Grid.Row>
                    <Grid.Column>
                        <Form.Field>
                            <label>Notes</label>
                            <textArea
                                name={notesId}
                                data-idx={idx}
                                id={notesId}
                                className="notes"
                                value={windowState[idx].notes}
                                onChange={handleWindowChange}
                            />
                        </Form.Field>
                    </Grid.Column>
                </Grid>
            </Segment>
            <br />
        </div >
    );
};

WindowInputs.propTypes = {
    idx: PropTypes.number,
    windowState: PropTypes.array,
    handleWindowChange: PropTypes.func,
};

export default WindowInputs;
