import React, { useState } from 'react';
import WindowInputs from './WindowInputs';

const Form = () => {
    const [ownerState, setOwnerState] = useState({
        owner: '',
        description: '',
    });

    const handleOwnerChange = (e) => setOwnerState({
        ...ownerState,
        [e.target.name]: [e.target.value],
    });

    const blankWindow = { name: '', age: '' };
    const [windowState, setWindowState] = useState([
        { ...blankWindow },
    ]);

    const addWindow = () => {
        setWindowState([...windowState, { ...blankWindow }]);
    };

    const handleWindowChange = (e) => {
        const updatedWindows = [...windowState];
        updatedWindows[e.target.dataset.idx][e.target.className] = e.target.value;
        setWindowState(updatedWindows);
    };

    const onSubmit = () => {
        console.log(windowState);
    }

    return (
        <form onSubmit={onSubmit}>
            <label htmlFor="owner">Owner</label>
            <input
                type="text"
                name="owner"
                id="owner"
                value={ownerState.owner}
                onChange={handleOwnerChange}
            />
            <label htmlFor="description">Description</label>
            <input
                type="text"
                name="description"
                id="description"
                value={ownerState.description}
                onChange={handleOwnerChange}
            />
            <input
                type="button"
                value="Add New Window"
                onClick={addWindow}
            />
            {
                windowState.map((val, idx) => (
                    <WindowInputs
                        key={`window-${idx}`}
                        idx={idx}
                        windowState={windowState}
                        handleWindowChange={handleWindowChange}
                    />
                ))
            }
            <input type="submit" value="Submit" />
        </form>
    );
};

export default Form;
