import React from 'react';
import PropTypes from 'prop-types';
import { Form, Input, Label } from "semantic-ui-react";

const WindowInputs = ({ idx, windowState, handleWindowChange }) => {
    const nameId = `name-${idx}`;
    const widthId = `width-${idx}`;
    const heightId = `height-${idx}`;

    return (
        <div key={`window-${idx}`}>
            <Form.Field>
                <Label style={{display: "inline-block", float: "left"}}>Name</Label>
                <input style={{display: "inline-block", float: "left"}}
                    name={nameId}
                    data-idx={idx}
                    id={nameId}
                    className="name"
                    value={windowState[idx].name}
                    onChange={handleWindowChange}
                />
            </Form.Field>
            <Form.Field>
                <Input 
                    label="Width"
                    name={widthId}
                    data-idx={idx}
                    id={widthId}
                    className="width"
                    value={windowState[idx].width}
                    onChange={handleWindowChange}
                />
            </Form.Field>
            <Form.Field >
                <Input 
                    label="Height"
                    name={heightId}
                    data-idx={idx}
                    id={heightId}
                    className="height"
                    value={windowState[idx].height}
                    onChange={handleWindowChange}
                />
            </Form.Field>
        </div>
    );
};

WindowInputs.propTypes = {
    idx: PropTypes.number,
    windowState: PropTypes.array,
    handleWindowChange: PropTypes.func,
};

export default WindowInputs;
