import React, { useState } from 'react';
import WindowInputs from './WindowInputs';
import { Form, Input, Button, Grid, FormField } from "semantic-ui-react";

const QuoteForm = () => {
    const [quoteState, setQuoteState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        address: {
            street: '',
            suburb: '',
            city: '',
            postCode: ''
        }
    });

    const handleOwnerChange = (e) => setQuoteState({
        ...quoteState,
        [e.target.name]: [e.target.value],
    });

    const blankWindow = { width: '2400', height: '1200' };
    const [windowState, setWindowState] = useState([
        { ...blankWindow },
    ]);

    const addWindow = () => {
        setWindowState([...windowState, { ...blankWindow }]);
    };

    const handleWindowChange = (e) => {
        const updatedWindows = [...windowState];
        updatedWindows[e.target.dataset.idx][e.target.className] = e.target.value;
        setWindowState(updatedWindows);
    };

    const onSubmit = () => {
        console.log("window state: " + windowState);
    }

    return (
        <Form>
            <Grid columns={2} padded="vertically">
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="First Name"
                            type="text"
                            name="firstName"
                            id="firstName"
                            value={quoteState.owner}
                            onChange={handleOwnerChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            label="Description"
                            type="text"
                            name="description"
                            id="description"
                            value={quoteState.description}
                            onChange={handleOwnerChange}
                        />
                    </Form.Field>
                </Grid.Column>
                <Grid.Column>
                    <Form.Field>
                        <Input
                            type="button"
                            value="Add New Window"
                            onClick={addWindow}
                        />
                    </Form.Field>
                </Grid.Column>
            </Grid>
            <Grid columns={1}>
                <Grid.Column>
                    {
                        windowState.map((val, idx) => (
                            <WindowInputs
                                key={`window-${idx}`}
                                idx={idx}
                                windowState={windowState}
                                handleWindowChange={handleWindowChange}
                            />
                        ))
                    }
                </Grid.Column>
            </Grid>
            <Input type="submit" value="Add Quote" onClick={onSubmit} />
        </Form>
    );
};

export default QuoteForm;
